CREATE TABLE EDGE (ID BIGINT NOT NULL AUTO_INCREMENT, CASCADED TINYINT(1), IDX VARCHAR(255), KIND INTEGER, VERSION INTEGER, TO_ID BIGINT NOT NULL, FROM_ID BIGINT NOT NULL, PRIMARY KEY (ID)) TYPE = innodb;
CREATE TABLE NODE (ID BIGINT NOT NULL AUTO_INCREMENT, COMMENT VARCHAR(255), DTCREATION BIGINT, DTUPDATE BIGINT, IDOWNER INTEGER, ISROOT TINYINT(1), NAME VARCHAR(255), VERSION INTEGER, NODETYPE_ID BIGINT, PRIMARY KEY (ID)) TYPE = innodb;
CREATE TABLE NODEFIELD (ID BIGINT NOT NULL AUTO_INCREMENT, FTYPE VARCHAR(255) NOT NULL, BLOB_ID BIGINT NOT NULL, NODE_ID BIGINT NOT NULL, PRIMARY KEY (ID)) TYPE = innodb;
CREATE TABLE NODEPROPERTY (ID BIGINT NOT NULL AUTO_INCREMENT, IDX VARCHAR(255), VALUE VARCHAR(255), VERSION INTEGER, NODE_ID BIGINT NOT NULL, PROP_ID BIGINT NOT NULL, PRIMARY KEY (ID)) TYPE = innodb;
CREATE TABLE NODEREVISION (ID BIGINT NOT NULL AUTO_INCREMENT, CONTEXT VARCHAR(255), REVISED_ID BIGINT NOT NULL, REVISION_ID BIGINT NOT NULL, PRIMARY KEY (ID)) TYPE = innodb;
CREATE TABLE NODETYPE (ID BIGINT NOT NULL AUTO_INCREMENT, NTYPE VARCHAR(255) NOT NULL, PRIMARY KEY (ID)) TYPE = innodb;
CREATE TABLE PROPERTY (ID BIGINT NOT NULL AUTO_INCREMENT, NAME VARCHAR(255) NOT NULL, VERSION INTEGER, PRIMARY KEY (ID)) TYPE = innodb;
CREATE TABLE PROPERTY_NODETYPE (PROPERTY_ID BIGINT, NODETYPE_ID BIGINT) TYPE = innodb;
CREATE TABLE SPLITBLOB (ID BIGINT NOT NULL AUTO_INCREMENT, NUMELT BIGINT, NUMELTEACHPART INTEGER, SHAPE VARCHAR(255) NOT NULL, PTYPE SMALLINT, VERSION INTEGER, PRIMARY KEY (ID)) TYPE = innodb;
CREATE TABLE SPLITBLOBPART (ID BIGINT NOT NULL AUTO_INCREMENT, DATA LONGBLOB, DATASIZE INTEGER, ORDR INTEGER, VERSION INTEGER, PARENT_ID BIGINT NOT NULL, PRIMARY KEY (ID)) TYPE = innodb;
CREATE INDEX I_EDGE_FROM ON EDGE (FROM_ID);
CREATE INDEX I_EDGE_TO ON EDGE (TO_ID);
CREATE INDEX I_NODE_NODETYPE ON NODE (NODETYPE_ID);
CREATE INDEX I_NODFILD_BLOB ON NODEFIELD (BLOB_ID);
CREATE INDEX I_NODFILD_NODE ON NODEFIELD (NODE_ID);
CREATE INDEX I_NDPRRTY_NODE ON NODEPROPERTY (NODE_ID);
CREATE INDEX I_NDPRRTY_PROP ON NODEPROPERTY (PROP_ID);
CREATE INDEX I_NDRVSON_REVISED ON NODEREVISION (REVISED_ID);
CREATE INDEX I_NDRVSON_REVISION ON NODEREVISION (REVISION_ID);
CREATE INDEX I_PRPRTYP_ELEMENT ON PROPERTY_NODETYPE (NODETYPE_ID);
CREATE INDEX I_PRPRTYP_PROPERTY_ID ON PROPERTY_NODETYPE (PROPERTY_ID);
CREATE INDEX I_SPLTPRT_PARENT ON SPLITBLOBPART (PARENT_ID);
